import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ReportPage() {
  const [presensiData, setPresensiData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedPhoto, setSelectedPhoto] = useState(null); // Modal foto

  useEffect(() => {
    fetchPresensiData();
  }, []);

  const fetchPresensiData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:3001/api/presensi/all', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setPresensiData(response.data.data);
      setLoading(false);
    } catch (err) {
      setError('Gagal memuat data presensi');
      setLoading(false);
      console.error(err);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleString('id-ID');
  };

  const openPhotoModal = (photoPath) => {
    setSelectedPhoto(photoPath);
  };

  const closePhotoModal = () => {
    setSelectedPhoto(null);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="text-xl">Memuat data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="text-xl text-red-500">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Laporan Presensi</h1>

      <div className="bg-white shadow-md rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full table-auto">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  User ID
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  Nama
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  Check In
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  Check Out
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  Lokasi
                </th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-700 uppercase tracking-wider">
                  Bukti Foto
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {presensiData.length === 0 ? (
                <tr>
                  <td colSpan="6" className="px-4 py-4 text-center text-gray-500">
                    Tidak ada data presensi
                  </td>
                </tr>
              ) : (
                presensiData.map((item) => (
                  <tr key={item.id} className="hover:bg-gray-50">
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {item.userId}
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {item.User?.nama || 'N/A'}
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {formatDate(item.checkIn)}
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {formatDate(item.checkOut)}
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {item.latitude && item.longitude ? (
                        <a
                          href={`https://www.google.com/maps?q=${item.latitude},${item.longitude}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:underline"
                        >
                          Lihat Peta
                        </a>
                      ) : (
                        '-'
                      )}
                    </td>
                    <td className="px-4 py-4 whitespace-nowrap text-sm">
                      {item.buktiFoto ? (
                        <img
                          src={`http://localhost:3001/${item.buktiFoto}`}
                          alt="Bukti Presensi"
                          className="w-16 h-16 object-cover rounded cursor-pointer hover:opacity-75 transition"
                          onClick={() => openPhotoModal(item.buktiFoto)}
                        />
                      ) : (
                        <span className="text-gray-400">Tidak ada foto</span>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal Foto Full Size */}
      {selectedPhoto && (
        <div
          className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
          onClick={closePhotoModal}
        >
          <div className="relative max-w-4xl max-h-screen p-4">
            <button
              onClick={closePhotoModal}
              className="absolute top-6 right-6 text-white text-3xl font-bold hover:text-gray-300 z-10"
            >
              &times;
            </button>
            <img
              src={`http://localhost:3001/${selectedPhoto}`}
              alt="Bukti Presensi Fullsize"
              className="max-w-full max-h-screen object-contain rounded-lg"
              onClick={(e) => e.stopPropagation()}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default ReportPage;
